def legendre(a, p):
    return pow(a, (p - 1) // 2, p)

def tonelli(n, p):
    assert legendre(n, p) == 1, "Not a square (mod p)"
    q, s = p - 1, 0
    while q % 2 == 0:
        q //= 2
        s += 1

    if s == 1:
        return pow(n, (p + 1) // 4, p)

    z = next(z for z in range(2, p) if legendre(z, p) == p - 1)
    c, r, t, m = pow(z, q, p), pow(n, (q + 1) // 2, p), pow(n, q, p), s

    while t % p != 1:
        t2 = t
        for i in range(1, m):
            t2 = pow(t2, 2, p)
            if t2 % p == 1:
                break
        b = pow(c, 1 << (m - i - 1), p)
        r, t, c, m = (r * b) % p, (t * b * b) % p, b * b % p, i

    return r

